import React, { useEffect, useState } from 'react';

import Tasks from './components/Tasks/Tasks';
import NewTask from './components/NewTask/NewTask';
import useHttp from './hooks/use-http';

function App() {
  const [tasks, setTasks] = useState([]);

  const transformTasks = tasksObj => {
    const loadedTasks = [];
    for (const taskKey in tasksObj) {
      loadedTasks.push({id:taskKey, text:tasksObj[taskKey].text})
    }
    setTasks(loadedTasks);
  }

  const httpData = useHttp({url: "https://tasks-custom-hooks-3d663-default-rtdb.firebaseio.com/tasks.json"}, transformTasks);

  const {isLoading, error, sendRequest:fetchTasks} = httpData; // destructuring a pridani alias k send request

  const taskAddHandler = (task) => {
    setTasks((prevTasks) => prevTasks.concat(task));
  };

  useEffect(() => {
    fetchTasks();
  }, []);


  return (
    <React.Fragment>
      <NewTask onAddTask={taskAddHandler} />
      <Tasks
        items={tasks}
        loading={isLoading}
        error={error}
        onFetch={fetchTasks}
      />
    </React.Fragment>
  );
}

export default App;
